mapper.insert:
INSERT INTO notice
SET calendar_id = #{calendarId}
    , content = #{content}

INSERT INTO notice_list
SET notice_id = #{noticeId}
, user_id = #{userId}

mapper.select:
SELECT user_id
FROM calendar_user
WHERE calendar_id = #{calendarId}

SELECT title
FROM calendar
WHERE calendar_id = #{calendarId}

SELECT name
FROM user
WHERE user_id = #{userId}

SELECT title
FROM board
WHERE board_id = #{boardId}
