mapper.insert:
INSERT INTO tag
SET calendar_id = #{calendarId}
    , title = #{title}
    , color = #{color}

mapper.select:
SELECT tag_id AS tagId, calendar_id AS calendarId, title, color
    , created_at AS createdAt, updated_at AS updatedAt
FROM tag
WHERE title = #{title}
    AND calendar_id = #{calendarId}

SELECT tag_id
FROM board_tag
WHERE tag_id = #{tagId}
